import React, { useState } from 'react';
import BpkCalendar, { CALENDAR_SELECTION_TYPE } from '@skyscanner/backpack-web/bpk-component-calendar';
import { BpkCode } from '@skyscanner/backpack-web/bpk-component-code';
import BpkText from '@skyscanner/backpack-web/bpk-component-text';
import BpkInput, {
  INPUT_TYPES,
} from '@skyscanner/backpack-web/bpk-component-input';
import BpkButton from '@skyscanner/backpack-web/bpk-component-button';

import format from 'date-fns/format';

import { cssModules } from '@skyscanner/backpack-web/bpk-react-utils';

import STYLES from './App.scss';

const getClassName = cssModules(STYLES);

const formatDateFull = (date) => format(date, 'EEEE, do MMMM yyyy');
const formatMonth = (date) => format(date, 'MMMM yyyy');
const daysOfWeek = [
  {
    name: 'Sunday',
    nameAbbr: 'Sun',
    index: 0,
    isWeekend: true,
  },
  // ...
];

const App = () => {
  const [ selectionConfiguration, setSelectionConfiguration ] = useState({
    type: CALENDAR_SELECTION_TYPE.single,
    date: null,
  }); 

  const handleDateSelect = (date) => {
    setSelectionConfiguration({
      type: selectionConfiguration.type,
      date: date,
    });
  }; 

    return (
      <div className={getClassName('App')}>

      <header className={getClassName('App__header')}>
        <div className={getClassName('App__header-inner')}>
          <BpkText tagName="h1" textStyle="xxl" className={getClassName('App__heading')}>Flight Schedule</BpkText>
        </div>
      </header>
      <main className={getClassName('App__main')}>
      <BpkText tagName="p" className={getClassName('App__text')}>
        To get started, edit <BpkCode>src/App.jsx</BpkCode> and save to reload.
      </BpkText>
      <BpkText tagName="p" className={getClassName('App__text')}>
        CALENDAR
      </BpkText>
      <BpkButton onClick={() => alert('It works!')}>Click me</BpkButton>
      <BpkInput
          id="dateInput"
          type={INPUT_TYPES.text}
          name="date"
          value={(selectionConfiguration.date || '').toString()}
          placeholder="Departure date"
        />
        <BpkCalendar
          id="calendar"
          onDateSelect={handleDateSelect}
          formatMonth={formatMonth}
          formatDateFull={formatDateFull}
          daysOfWeek={daysOfWeek}
          weekStartsOn={1}
          changeMonthLabel="Change month"
          nextMonthLabel="Next month"
          previousMonthLabel="Previous month"
          selectionConfiguration={selectionConfiguration}
        />
      </main>

      </div>
    );

  };

  export default App;


/* steps 
DONE - Step One: Update the header text to read “Flight Schedule”

DONE - Step Two: Install the Calendar component by running the command `npm install bpk-component-calendar --save-dev`. This is also described in the Calendar documentation.

Step Three: Review the Calendar documentation for usage, and add this feature into your `App.js` file. You can also edit the `App.scss` to adjust the calendar’s appearance.

Step Four: Place the “Click Me” button below the calendar, and update the button’s text to say “Continue.” You can leave the button’s functionality as is.

Step Five: As described above, review your application’s appearance by entering `npm start` in the terminal and then navigating to localhost:3000. */
